using UnityEngine;
using MoralisUnity;
using Nethereum.Hex.HexTypes;
using MoralisUnity.Platform.Objects;
using MoralisUnity.Platform.Queries;
using Cysharp.Threading.Tasks;

/// <summary>
/// Mints an Arena Access NFT to the player connected wallet address if they have the sufficient funds. This grants them access to the arena to compete.
/// </summary>
public class ArenaNFT : MonoBehaviour, IClick
{
    public class ArenaGateEvent : MoralisObject
    {
        public bool result { get; set; }
        
        public ArenaGateEvent() : base("ArenaGateEvent") {}
    }

    // Deployed contract on Mumbai testnet
    private const string contractAddress = "0xEE4b72cE7543b62a738E24519B27ac1775c90fCE";
